using System.Collections; using System.Collections.Generic; using UnityEngine;  public class ActivateObjects : MonoBehaviour {  	public bool isArrowActive;  	GameObject[] gameObjectArray;  	void Start () { 		isArrowActive = true; 		gameObjectArray = GameObject.FindGameObjectsWithTag ("Arrow"); 	}  	public void DisableArrows() 	{ 		gameObjectArray = GameObject.FindGameObjectsWithTag ("Arrow");  		foreach (GameObject go in gameObjectArray)  		{ 			go.SetActive (false); 		}  	}  	public void Activate() 	{ 		GameObject[] NEWgameObjectArray = GameObject.FindGameObjectsWithTag ("Arrow"); 		foreach (GameObject go in NEWgameObjectArray) 		{ 			go.SetActive (false); 		} 	}  	void Update() { 		if (Input.GetKeyDown (KeyCode.D)) { 				DisableArrows (); 			}  		if (Input.GetKeyDown (KeyCode.A)) { 			Activate (); 		}  		} 	}  